import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api';

interface TutorProfile {
    id: number;
    headline: string;
    description: string;
    hourlyRate: number;
    user: { id: number; username: string; };
}

interface Availability {
    id: number;
    startTime: string;
    endTime: string;
    isBooked: boolean;
}

interface Review {
    id: number;
    rating: number;
    comment: string;
    student: { username: string; };
}

const TutorProfilePage: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [profile, setProfile] = useState<TutorProfile | null>(null);
    const [availability, setAvailability] = useState<Availability[]>([]);
    const [reviews, setReviews] = useState<Review[]>([]);

    useEffect(() => {
        if (id) {
            fetchProfile();
            fetchAvailability();
            fetchReviews();
        }
    }, [id]);

    const fetchProfile = async () => {
        try {
            const response = await api.get(`/api/profiles/tutor/${id}`);
            setProfile(response.data);
        } catch (error) {
            console.error('Failed to fetch profile', error);
        }
    };

    const fetchAvailability = async () => {
        try {
            const response = await api.get(`/api/tutor/${id}/availability`);
            setAvailability(response.data);
        } catch (error) {
            console.error('Failed to fetch availability', error);
        }
    };

    const fetchReviews = async () => {
        try {
            const response = await api.get(`/api/reviews/tutor/${id}`);
            setReviews(response.data);
        } catch (error) {
            console.error('Failed to fetch reviews', error);
        }
    };

    const handleBookSlot = async (slotId: number) => {
        try {
            await api.post(`/api/student/book?slotId=${slotId}`);
            alert('Booking successful!');
            fetchAvailability(); // Refresh availability
        } catch (error) {
            console.error('Booking failed', error);
            alert('Booking failed. The slot might be already taken.');
        }
    };

    if (!profile) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>{profile.headline}</h2>
            <p>Tutor: {profile.user.username}</p>
            <p>{profile.description}</p>
            <p>Rate: ${profile.hourlyRate}/hr</p>

            <hr />

            <h3>Availability</h3>
            {availability.filter(slot => !slot.isBooked).map(slot => (
                <div key={slot.id}>
                    <span>{new Date(slot.startTime).toLocaleString()} - {new Date(slot.endTime).toLocaleString()}</span>
                    <button onClick={() => handleBookSlot(slot.id)}>Book</button>
                </div>
            ))}

            <hr />

            <h3>Reviews</h3>
            {reviews.map(review => (
                <div key={review.id}>
                    <p><strong>{review.student.username}</strong> ({review.rating}/5)</p>
                    <p>{review.comment}</p>
                </div>
            ))}
        </div>
    );
};

export default TutorProfilePage;
