// frontend/src/pages/StudentHome.tsx
import { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function StudentHome() {
  const [tutors, setTutors] = useState<any[]>([]);
  const [searchTerm, setSearchTerm] = useState(''); // ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏ï‡∏±‡∏ß‡πÅ‡∏õ‡∏£‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
  const navigate = useNavigate();
  
  // ‡∏î‡∏∂‡∏á‡∏ä‡∏∑‡πà‡∏≠ user ‡∏°‡∏≤‡πÇ‡∏ä‡∏ß‡πå‡πÉ‡∏´‡πâ‡∏î‡∏π‡πÄ‡∏õ‡πá‡∏ô‡∏Å‡∏±‡∏ô‡πÄ‡∏≠‡∏á
  const username = localStorage.getItem('username') || '‡∏ô‡∏±‡∏Å‡πÄ‡∏£‡∏µ‡∏¢‡∏ô';

  useEffect(() => {
    // ‡∏î‡∏∂‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏¥‡∏ß‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î
    axios.get('http://localhost:8080/api/profiles/tutor')
      .then(res => setTutors(res.data))
      .catch(err => console.error(err));
  }, []);

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô Logout
  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  // ‡∏ü‡∏±‡∏á‡∏Å‡πå‡∏ä‡∏±‡∏ô‡∏Å‡∏£‡∏≠‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• (Search Logic)
  const filteredTutors = tutors.filter(tutor => {
    const text = searchTerm.toLowerCase();
    return (
      tutor.headline?.toLowerCase().includes(text) || // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏à‡∏≤‡∏Å‡∏´‡∏±‡∏ß‡∏Ç‡πâ‡∏≠
      tutor.user?.username?.toLowerCase().includes(text) || // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏à‡∏≤‡∏Å‡∏ä‡∏∑‡πà‡∏≠‡∏Ñ‡∏£‡∏π
      tutor.description?.toLowerCase().includes(text) // ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏à‡∏≤‡∏Å‡∏Ñ‡∏≥‡∏ö‡∏£‡∏£‡∏¢‡∏≤‡∏¢
    );
  });

  return (
    <div className="bg-gray-50 min-h-screen">
      
      {/* üü¢ ‡∏™‡πà‡∏ß‡∏ô Header ‡∏î‡πâ‡∏≤‡∏ô‡∏ö‡∏ô (‡πÅ‡∏ó‡∏ô Navbar) */}
      <div className="bg-white shadow-sm sticky top-0 z-10">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-xl font-bold text-blue-600 flex items-center gap-2">
            üéì TutorMatch
          </h1>
          
          <div className="flex items-center gap-4">
            <span className="text-gray-600 hidden sm:inline">‡∏™‡∏ß‡∏±‡∏™‡∏î‡∏µ, <b>{username}</b></span>
            
            {/* ‡∏õ‡∏∏‡πà‡∏°‡πÑ‡∏õ‡∏î‡∏π‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏£‡∏µ‡∏¢‡∏ô */}
            <button 
              onClick={() => navigate('/student-schedule')}
              className="text-blue-600 hover:bg-blue-50 px-3 py-2 rounded font-medium transition"
            >
              üìÖ ‡∏ï‡∏≤‡∏£‡∏≤‡∏á‡πÄ‡∏£‡∏µ‡∏¢‡∏ô‡∏Ç‡∏≠‡∏á‡∏â‡∏±‡∏ô
            </button>

            {/* ‡∏õ‡∏∏‡πà‡∏° Logout */}
            <button 
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 text-sm transition"
            >
              Logout
            </button>
          </div>
        </div>
      </div>

      <div className="container mx-auto p-6">
        
        {/* üîç ‡∏™‡πà‡∏ß‡∏ô‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤ */}
        <div className="bg-white p-8 rounded-lg shadow-md mb-8 text-center">
          <h1 className="text-3xl font-bold text-blue-600 mb-4">‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏ï‡∏¥‡∏ß‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πà‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏Ñ‡∏∏‡∏ì</h1>
          <div className="flex justify-center gap-2 max-w-2xl mx-auto">
            <input 
              type="text" 
              placeholder="‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏ä‡∏∑‡πà‡∏≠‡∏ß‡∏¥‡∏ä‡∏≤, ‡∏ä‡∏∑‡πà‡∏≠‡∏Ñ‡∏£‡∏π, ‡∏´‡∏£‡∏∑‡∏≠‡∏ó‡∏±‡∏Å‡∏©‡∏∞ (‡πÄ‡∏ä‡πà‡∏ô Math, English)" 
              className="border p-3 rounded-l-lg w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)} // ‡∏û‡∏¥‡∏°‡∏û‡πå‡∏õ‡∏∏‡πä‡∏ö ‡∏Å‡∏£‡∏≠‡∏á‡∏õ‡∏±‡πä‡∏ö
            />
            <button className="bg-blue-600 text-white px-6 py-3 rounded-r-lg hover:bg-blue-700 font-bold">
              ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
            </button>
          </div>
        </div>

        {/* üåü ‡∏™‡πà‡∏ß‡∏ô‡πÅ‡∏™‡∏î‡∏á‡∏£‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠ (‡πÉ‡∏ä‡πâ filteredTutors ‡πÅ‡∏ó‡∏ô tutors ‡∏õ‡∏Å‡∏ï‡∏¥) */}
        <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold text-gray-800">üåü ‡∏ï‡∏¥‡∏ß‡πÄ‡∏ï‡∏≠‡∏£‡πå‡πÅ‡∏ô‡∏∞‡∏ô‡∏≥</h2>
            <span className="text-gray-500 text-sm">‡∏Ñ‡πâ‡∏ô‡∏û‡∏ö {filteredTutors.length} ‡∏Ñ‡∏ô</span>
        </div>

        {filteredTutors.length === 0 ? (
            <div className="text-center py-10 text-gray-500">
                ‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏¥‡∏ß‡πÄ‡∏ï‡∏≠‡∏£‡πå‡∏ó‡∏µ‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö‡∏Ñ‡∏≥‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
            </div>
        ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredTutors.map((tutor) => (
                <div key={tutor.id} className="bg-white p-6 rounded-lg shadow hover:shadow-lg transition duration-200 border border-gray-100 flex flex-col justify-between">
                <div>
                    <div className="flex items-center gap-3 mb-3">
                        <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center text-blue-600 font-bold">
                            {tutor.user?.username?.charAt(0).toUpperCase()}
                        </div>
                        <div>
                            <h3 className="font-bold text-lg text-gray-800">{tutor.user?.username}</h3>
                            <p className="text-xs text-gray-500">Tutor</p>
